IF EXISTS(SELECT 1 FROM sys.tables WHERE object_id = OBJECT_ID('Statistics'))
BEGIN;
    DROP TABLE [Statistics];
END;
GO

CREATE TABLE[Statistics](
Write_Id  integer,--Id записи
User_Id  integer  NOT NULL,--Id пользователя
Study_time  integer  NOT NULL,--Время_учебы
Start_study_time datetime NOT NULL,--Дата_начала_учебы
CONSTRAINT[PK_Statistics]PRIMARY KEY CLUSTERED
(
[Write_Id]ASC
)WITH(IGNORE_DUP_KEY=OFF)

)
GO

